import './App.css';
import React from 'react';
import AutoCompleteInput from './AutoCompleteInput';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {};
  }

  showResults(e) {
   var res = document.getElementById("result");

   res.innerHTML = '';

   if (e.target.value === '') {
     return;
   }

   let list = '';

   fetch('https://localhost:44315/api/Cities/' + e.target.value).then(function (response) {
     return response.json();
   }).then(function (data) {
     for (let i = 0; i < data.length; i++) {
       list += '<li>' + data[i]["name"] + ', ' + data[i]["subCountry"] + ', ' + data[i]["country"] +  '</li>';
     }
     res.innerHTML = '<ul>' + list + '</ul>';
     return true;
   }).catch(function (err) {
     console.warn('Something went wrong.', err);
     return false;
   });
  }

  render() {
    return (
      <div className="App">
        <header>
        </header>

        <AutoCompleteInput/>

        <form autoComplete="off">
          <input type="text" name="q" id="q" onKeyUp={this.showResults} />
          <div id="result"></div>
        </form>

      </div>
    );
  }
}

export default App;
