import React from "react";

export default class AutoCompleteInput extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            suggestions: []
        };
    }

    onTextChange = (e) => {
        const value = e.target.value;

        if(value.length === 0){
            this.setState(() => ({
                suggestions: []
            }))
        }else{
            const suggestions = [];
            fetch('https://localhost:44315/api/Cities/' + e.target.value).then(response => 
                response.json()
            ).then(data => this.setState(() => ({
                suggestions
            })));
        }

        console.log(this.state);
    }

    showResults(e) {
        var res = document.getElementById("result");
        
        res.innerHTML = '';

        if (e.target.value === '') {
            return;
        }

        let list = '';  
        
        fetch('https://localhost:44315/api/Cities/' + e.target.value).then(function (response) {
            return response.json();
        }).then(function (data) {
            for (let i = 0; i < data.length; i++) {
                list += '<li>' + data[i]["name"] + ', ' + data[i]["subCountry"] + ', ' + data[i]["country"] +  '</li>';
            }
            res.innerHTML = '<ul>' + list + '</ul>';
            return true;
        }).catch(function (err) {
            console.warn('Something went wrong.', err);
            return false;
        });
    }

    render() {
        return (
            <div>
                <input type="text" onChange={this.onTextChange} onKeyUp={this.showResults} />
                <div id="result"></div>
            </div>
        )
    }
}

//suspend till user finish to type to minimize networke calls
//typos - Though “iphone” is misspelled, the results flexibly assume what the user meant
//category - For some terms, the user can drill down by category for a more specific search
//unit tests
//data source improvment - federated search
