import React from "react";

export default class AutoCompleteInput extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            items: [],
        };
    }

    showResults(e) {
        var res = document.getElementById("result");
        
        res.innerHTML = '';
        
        if (e.target.value === '') {
            return;
        }
        
        let list = '';
        
        fetch('https://localhost:44315/api/Cities/' + e.target.value).then(function (response) {
            return response.json();
        }).then(function (data) {
            let items = [];
            for (let i = 0; i < data.length; i++) {
                this.setState(() => ({ items }));
                list += '<li>' + data[i]["name"] + ', ' + data[i]["subCountry"] + ', ' + data[i]["country"] +  '</li>';
            }
            res.innerHTML = '<ul>' + list + '</ul>';
            return true;
        }).catch(function (err) {
            console.warn('Something went wrong.', err);
            return false;
        });
    }

    renderItems(){
        const { stateItems } = this.state;
        if (stateItems.length === 0){
            return null;
        }

        return (
            <ul>
                {stateItems.map((item) => <li>(item)</li>)}
            </ul>
        );
    }

    render() {
        return (
            // <form autoComplete="off">
            <div>
                <input type="text" onKeyUp={this.showResults} />
                {this.renderItems()}
                <div id="result"></div>
            </div>
            // </form>
        )
    }
}
