import React from "react";

export default class AutoCompleteInput extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            items: [],
        };
    }

    showResults = (e) => {
        var res = document.getElementById("result");
        
        res.innerHTML = '';
        
        if (e.target.value === '') {
            return;
        }
         
        let list = '';
        
        let items = [];     
        
        fetch('https://localhost:44315/api/Cities/' + e.target.value).then(function (response) {
            return response.json();
        }).then(function (data) {
            for (let i = 0; i < data.length; i++) {
                items.push(data[i]["name"] + ', ' + data[i]["subCountry"] + ', ' + data[i]["country"]);
                list += '<li>' + data[i]["name"] + ', ' + data[i]["subCountry"] + ', ' + data[i]["country"] +  '</li>';
            }
            res.innerHTML = '<ul>' + list + '</ul>';
            
            return true;
        }).catch(function (err) {
            console.warn('Something went wrong.', err);
            return false;
        });

        this.setState(() => ({ items }));
    }

    renderItems(){
        const { items } = this.state;
        if (items.length === 0){
            return null;
        }

        return (
            <ul>
                {items.map((item) => <li>(item)</li>)}
            </ul>
        );
    }

    render() {
        return (
            // <form autoComplete="off">
            <div>
                <input type="text" onKeyUp={this.showResults} />
                {this.renderItems()}
                <div id="result"></div>
            </div>
            // </form>
        )
    }
}
