import { getSuggestedQuery } from "@testing-library/react";
import React from "react";

export default class AutoCompleteInput extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            suggestions: [],
            text: ''
        };
    }


    onTextChange = (e) => {
        const value = e.target.value;
        let suggestions = [];
        
        if(value.length > 0){
            fetch('https://localhost:44315/api/Cities/' + e.target.value).then(response => 
                response.json()
            ).then(data => {
                if(data.length > 0){
                    for (let i = 0; i < data.length; i++) {
                        suggestions.push(data[i]["name"] + ', ' + data[i]["subCountry"] + ', ' + data[i]["country"]);
                    } 
                
                    this.setState(() => ({suggestions, text: value}));    
                }else{
                    this.suggestionSelected(value);
                }
            });
        }
        else{
            this.suggestionSelected(value);
        }
    }

    suggestionSelected(value){
        this.setState(() => ({
            suggestions: [],
            text: value
        }))
    }

    renderSuggestion(){
        const { suggestions } = this.state;

        if(suggestions.length === 0){
            return null;
        }

        return(
            <div id="result">
                <ul>
                    {suggestions.map((item => <li onClick={() => this.suggestionSelected(item)}>{item}</li>))}
                </ul> 
            </div>
        );
    }
    
    render() {
        const {text} = this.state;
            
        const [query, setQuery] = useState("");
        const [displayMessage, setDisplayMessage] = useState("");

        useEffect(() => {
            const timeOutId = setTimeout(() => setDisplayMessage(query), 500);
            return () => clearTimeout(timeOutId);
        }, [query]);
        
        return (
            <div>
                <input type="text" value={text} onChange={this.onTextChange}/>
                <input type="text" value={query} onChange={event => setQuery(event.target.value)}/>
                {this.renderSuggestion()}
            </div>
        )
    }
}

