import './App.css';
import React from 'react';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {apiResponse : ""};
  }

  callAPI(){
    fetch("http://localhost:9000/API")
    .then(res => res.text())
    .then(res => this.setState({apiResponse : res}))
  }

  componentWillMount(){
    this.callAPI();
  }

   showResults() {
    var res = document.getElementById("result");

    res.innerHTML = '';

    if (this.value == '') {
      return;
    }

    let list = '';
    fetch('http://localhost:9000/suggest?q=' + this.value).then(function (response) {
      return response.json();
    }).then(function (data) {
      for (let i = 0; i < data.length; i++) {
        list += '<li>' + data[i] + '</li>';
      }

      res.innerHTML = '<ul>' + list + '</ul>';

      return true;
    }).catch(function (err) {
      console.warn('Something went wrong.', err);

      return false;
    });
  }

  render() {
    return (
      <div className="App">

        <header className="App-header">
        </header>

        <p>{this.state.apiResponse}</p>

        <form autocomplete="off">
          <input type="text" name="q" id="q" onChange={this.showResults} />
          <div id="result"></div>
        </form>

      </div>
    );
  }
}

export default App;
