import React from "react";
import { throttle } from "throttle-debounce";

export default class AutoCompleteInput extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            suggestions: [],
            text: ''
        };
        this.autocompleteSearchThrottled = throttle(3000, this.autocompleteSearch);
    }

    onTextChange = event => {
        this.setState({ text: event.target.value }, () => {
        this.autocompleteSearchThrottled(this.state.text);
        });
    };

    autocompleteSearch = (value) => {
        let suggestions = [];
        
        if(value.length > 0){
            fetch('https://localhost:44315/api/Cities/' + value).then(response => 
                response.json()
            ).then(data => {
                if(data.length > 0){
                    for (let i = 0; i < data.length; i++) {
                        suggestions.push(data[i]["name"] + ', ' + data[i]["subCountry"] + ', ' + data[i]["country"]);
                    } 
                    this.setState(() => ({suggestions}));    
                }else{
                    this.suggestionSelected(value);
                }
            });
        }
        else{
            this.suggestionSelected(value);
        }
    }

    suggestionSelected(value){
        this.setState(() => ({
            suggestions: [],
            text: value
        }))
    }

    renderSuggestion(){
        const { suggestions } = this.state;

        if(suggestions.length === 0){
            return null;
        }

        return(
            <div id="result">
                <ul>
                    {suggestions.map((item => <li onClick={() => this.suggestionSelected(item)}>{item}</li>))}
                </ul> 
            </div>
        );
    }
    
    render() {
        const {text} = this.state;
        
        return (
            <div>
                <input type="text" value={text} onChange={this.onTextChange}/>
                {this.renderSuggestion()}        
                <div>
                    <button > Add A City </button>
                </div>       
            </div>           
        )
    }
}

//cache - redis cache or in memory cache - if not exist in cache goes to database, clean cache by user and by time on appsettings
